// src/components/DepartmentManager.jsx
import React, { useState, useEffect } from 'react';
import { Button, Table, Modal, Form } from 'react-bootstrap';
import { getDepartments, createDepartment, updateDepartment, deleteDepartment } from '../services/departmentService';

const DepartmentManager = () => {
  const [departments, setDepartments] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [departmentData, setDepartmentData] = useState({ name: '', manager: '' });
  
  useEffect(() => {
    loadDepartments();
  }, []);

  const loadDepartments = async () => {
    const data = await getDepartments();
    setDepartments(data);
  };

  const handleCreateOrUpdate = () => {
    if (isEdit) {
      updateDepartment(departmentData)
        .then(() => {
          loadDepartments();
          setShowModal(false);
        })
        .catch((error) => console.error("Error updating department:", error));
    } else {
      createDepartment(departmentData)
        .then(() => {
          loadDepartments();
          setShowModal(false);
        })
        .catch((error) => console.error("Error creating department:", error));
    }
  };

  const handleDelete = (id) => {
    deleteDepartment(id)
      .then(() => loadDepartments())
      .catch((error) => console.error("Error deleting department:", error));
  };

  const handleModalShow = (department = null) => {
    if (department) {
      setIsEdit(true);
      setDepartmentData(department);
    } else {
      setIsEdit(false);
      setDepartmentData({ name: '', manager: '' });
    }
    setShowModal(true);
  };

  return (
    <div className="dashboard-container">
      <h3>Manage Departments</h3>
      <Button variant="primary" onClick={() => handleModalShow()}>
        Create Department
      </Button>
      <Table striped bordered hover className="mt-3">
        <thead>
          <tr>
            <th>Name</th>
            <th>Manager</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {departments.map((department) => (
            <tr key={department.id}>
              <td>{department.name}</td>
              <td>{department.manager}</td>
              <td>
                <Button variant="warning" onClick={() => handleModalShow(department)}>
                  Edit
                </Button>
                <Button variant="danger" onClick={() => handleDelete(department.id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal for creating and updating departments */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{isEdit ? 'Edit Department' : 'Create Department'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formDepartmentName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter department name"
                value={departmentData.name}
                onChange={(e) => setDepartmentData({ ...departmentData, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="formDepartmentManager">
              <Form.Label>Manager</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter manager's name"
                value={departmentData.manager}
                onChange={(e) => setDepartmentData({ ...departmentData, manager: e.target.value })}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCreateOrUpdate}>
            {isEdit ? 'Update Department' : 'Create Department'}
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default DepartmentManager;
