import axios from 'axios';

const apiUrl = 'http://localhost:5000/api/leaves';

export const getLeaveRequests = async () => {
  const response = await axios.get(apiUrl);
  return response.data;
};

export const approveLeaveRequest = async (id) => {
  const response = await axios.put(`${apiUrl}/${id}/approve`);
  return response.data;
};

export const rejectLeaveRequest = async (id) => {
  const response = await axios.put(`${apiUrl}/${id}/reject`);
  return response.data;
};

export const deleteLeaveRequest = async (id) => {
  await axios.delete(`${apiUrl}/${id}`);
};
