import React from 'react';
import { Nav } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = () => {
  return (
    <div className="sidebar">
      <h4 className="text-white p-3">Admin Panel</h4>
      <Nav className="flex-column px-3">
        <Nav.Link as={Link} to="/" className="text-white">Dashboard</Nav.Link>
        <Nav.Link as={Link} to="/employee" className="text-white">Employee</Nav.Link>
        <Nav.Link as={Link} to="/departments" className="text-white">Departments</Nav.Link>
        <Nav.Link as={Link} to="/roles" className="text-white">Roles</Nav.Link>
        <Nav.Link as={Link} to="/reports" className="text-white">Reports</Nav.Link>
        <Nav.Link as={Link} to="/salary" className="text-white">Salary</Nav.Link>
        <Nav.Link as={Link} to="/leave-requests" className="text-white">Leave Requests</Nav.Link>
      </Nav>
    </div>
  );
};

export default Sidebar;
