import axios from 'axios';

const apiUrl = 'http://localhost:5000/api/employees';

// Merr të gjithë punonjësit
export const getEmployees = async () => {
  try {
    const response = await axios.get(apiUrl);
    return response.data;
  } catch (error) {
    throw error;
  }
};

// Shto një punonjës të ri
export const createEmployee = async (employee) => {
  try {
    const response = await axios.post(apiUrl, employee);
    return response.data;
  } catch (error) {
    throw error;
  }
};

// Përditëso një punonjës ekzistues
export const updateEmployee = async (id, employee) => {
  try {
    const response = await axios.put(`${apiUrl}/${id}`, employee);
    return response.data;
  } catch (error) {
    throw error;
  }
};

// Fshi një punonjës
export const deleteEmployee = async (id) => {
  try {
    await axios.delete(`${apiUrl}/${id}`);
  } catch (error) {
    throw error;
  }
};
