// src/components/LeaveRequestManager.jsx
import React, { useState, useEffect } from 'react';
import { Table, Button } from 'react-bootstrap';
import { getLeaveRequests, approveLeaveRequest, rejectLeaveRequest } from '../services/leaveService';

const LeaveRequestManager = () => {
  const [leaveRequests, setLeaveRequests] = useState([]);

  useEffect(() => {
    getLeaveRequests()
      .then((data) => setLeaveRequests(data))
      .catch((error) => console.error("Error fetching leave requests:", error));
  }, []);

  const handleApprove = (id) => {
    approveLeaveRequest(id)
      .then(() => {
        setLeaveRequests(
          leaveRequests.map((request) =>
            request.id === id ? { ...request, status: 'Approved' } : request
          )
        );
      })
      .catch((error) => console.error("Error approving leave request:", error));
  };

  const handleReject = (id) => {
    rejectLeaveRequest(id)
      .then(() => {
        setLeaveRequests(
          leaveRequests.map((request) =>
            request.id === id ? { ...request, status: 'Rejected' } : request
          )
        );
      })
      .catch((error) => console.error("Error rejecting leave request:", error));
  };

  return (
    <div className="dashboard-container">
      <h3>Leave Requests</h3>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Employee Name</th>
            <th>Start Date</th>
            <th>End Date</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {leaveRequests.map((request) => (
            <tr key={request.id}>
              <td>{request.employeeName}</td>
              <td>{request.startDate}</td>
              <td>{request.endDate}</td>
              <td>{request.status}</td>
              <td>
                {request.status === 'Pending' && (
                  <>
                    <Button variant="success" onClick={() => handleApprove(request.id)}>
                      Approve
                    </Button>
                    <Button variant="danger" onClick={() => handleReject(request.id)}>
                      Reject
                    </Button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default LeaveRequestManager;
