// src/components/RoleManager.jsx
import React, { useState, useEffect } from 'react';
import { Button, Form, Table } from 'react-bootstrap';
import { getRoles, createRole, updateRole, deleteRole } from '../services/roleService';

const RoleManager = () => {
  const [roles, setRoles] = useState([]);
  const [roleData, setRoleData] = useState({
    name: '',
    privileges: '',
  });

  const loadRoles = async () => {
    const data = await getRoles();
    setRoles(data);
  };

  useEffect(() => {
    loadRoles();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRoleData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (roleData.id) {
      await updateRole(roleData);
    } else {
      await createRole(roleData);
    }
    loadRoles();
    setRoleData({
      name: '',
      privileges: '',
    });
  };

  const handleEdit = (role) => {
    setRoleData({
      id: role.id,
      name: role.name,
      privileges: role.privileges,
    });
  };

  const handleDelete = async (id) => {
    await deleteRole(id);
    loadRoles();
  };

  return (
    <div className="dashboard-container">
      <h3>Role Manager</h3>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="name">
          <Form.Label>Role Name</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={roleData.name}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="privileges">
          <Form.Label>Role Privileges</Form.Label>
          <Form.Control
            as="textarea"
            name="privileges"
            value={roleData.privileges}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit" className="mt-3">
          {roleData.id ? 'Update Role' : 'Create Role'}
        </Button>
      </Form>

      <h4 className="mt-4">Roles List</h4>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Role Name</th>
            <th>Privileges</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {roles.map((role) => (
            <tr key={role.id}>
              <td>{role.name}</td>
              <td>{role.privileges}</td>
              <td>
                <Button variant="info" onClick={() => handleEdit(role)}>
                  Edit
                </Button>
                <Button variant="danger" className="ml-2" onClick={() => handleDelete(role.id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default RoleManager;
